angular.module("starter").factory("Topic",function($pwaRequest,$session){var factory={value_id:null,extendedOptions:{}};return factory.setValueId=function(value_id){factory.value_id=value_id},factory.setExtendedOptions=function(options){factory.extendedOptions=options},factory.findAll=function(){return this.value_id?$pwaRequest.get("topic/mobile_list/findall",angular.extend({urlParams:{value_id:this.value_id,device_uid:$session.getDeviceUid()}},factory.extendedOptions)):$pwaRequest.reject("[Factory::Topic.findAll] missing value_id")},factory.subscribe=function(topic_id,is_subscribed){return $pwaRequest.post("topic/mobile_list/subscribe",{data:{category_id:topic_id,device_uid:$session.getDeviceUid(),subscribe:is_subscribed}})},factory});